INSERT INTO `tbl_news` (`pk_news_id`, `fk_category_news_id`, `c_name`, `c_description`, `c_content`, `c_img`, `c_hotnews`) VALUES
(7, 1, 'MẶC VEST NAM ĐẸP CHUẨN PHONG CÁCH Ý', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ng&agrave;y nay,&nbsp;Vest v&agrave; Suit&nbsp;l&agrave; những từ kh&ocirc;ng c&ograve;n qu&aacute; xa lạ đối với c&aacute;nh m&agrave;y r&acirc;u. Đẳng cấp v&agrave; phong độ của một người đ&agrave;n &ocirc;ng được thể hiện r&otilde;. </span></span></p>\r\n', '<p><strong>Bạn&nbsp;c&oacute; thể &aacute;p dụng một số tips sau để c&oacute; bộ vest đẹp đ&uacute;ng chuẩn:</strong></p>\r\n\r\n<p>1.&nbsp;Một bộ Vest nam đẹp đ&uacute;ng chuẩn cần phải vừa vặn với th&acirc;n h&igrave;nh v&agrave; t&ocirc;n l&ecirc;n v&oacute;c d&aacute;ng của bạn. Ngo&agrave;i ra, &aacute;o phải phẳng m&agrave; kh&ocirc;ng được nhăn nh&uacute;m khi bạn mặc c&agrave;i khuy.</p>\r\n\r\n<p>2.&nbsp;Khi nh&igrave;n v&agrave;o một bộ Vest, bao giờ người ta cũng nh&igrave;n cầu vai đầu ti&ecirc;n, ch&iacute;nh v&igrave; thế cầu vai &aacute;o Vest phải vừa vặn với tư thế v&agrave; t&ocirc;n l&ecirc;n d&aacute;ng điệu của người mặc. Một y&ecirc;u cầu kh&aacute;c nữa của chiếc &aacute;o Vest chuẩn l&agrave; miếng đệm vai kh&ocirc;ng được trễ xuống hay tạo vết l&otilde;m tr&ecirc;n vai m&agrave; cần phải vừa vặn tr&ecirc;n vai bạn.</p>\r\n\r\n<p>3.&nbsp;Vai &aacute;o Vest phải &ocirc;m v&agrave;o vai chứ kh&ocirc;ng được &eacute;p chặt hay thừa ra. Độ xu&ocirc;i vai cũng cần phải lưu &yacute;, nếu độ xu&ocirc;i vai kh&ocirc;ng đ&uacute;ng sẽ l&agrave;m vạt &aacute;o bị tỏa ra th&agrave;nh h&igrave;nh chữ V khi kh&ocirc;ng c&agrave;i n&uacute;t chứ kh&ocirc;ng thẳng như d&aacute;ng người hoặc khi c&agrave;i n&uacute;t th&igrave; hai vạt &aacute;o sẽ bị loe ra, bị x&ocirc; lệch chứ kh&ocirc;ng thẳng xu&ocirc;i theo th&acirc;n &aacute;o.</p>\r\n\r\n<p>4.&nbsp;Cổ &aacute;o cũng l&agrave; điểm cần lưu &yacute; khi chọn Vest. Cổ &aacute;o phải &ocirc;m s&aacute;t g&aacute;y kết hợp với cổ &aacute;o sơ mi đi k&egrave;m cao hơn khoảng 1cm.</p>\r\n\r\n<p>5.&nbsp;Phần tay của bộ Vest phải phẳng phiu,&nbsp;mộng vai tr&ograve;n đều, kh&ocirc;ng nhăn nh&uacute;m. Khi chọn &aacute;o vest, c&aacute;c ch&agrave;ng n&ecirc;n để &yacute; chiều d&agrave;i của &aacute;o. Chiều d&agrave;i chuẩn cho một chiếc &aacute;o vest lịch sự l&agrave; tay c&oacute; thể chạm v&agrave;o đu&ocirc;i &aacute;o khi duỗi thẳng. Với những kiểu vest kho&aacute;c m&ugrave;a h&egrave; hoặc ph&aacute; c&aacute;ch để trẻ trung hơn th&igrave; c&oacute; thể thu ngắn chiều d&agrave;i &aacute;o lại.</p>\r\n\r\n<p>6.&nbsp;Những bộ vest nam đẹp th&igrave; chiều d&agrave;i cổ tay &aacute;o sơ mi trong n&ecirc;n lộ ra so với chiều d&agrave;i tay &aacute;o comple khoảng 1 đến 2cm. C&ograve;n chiều d&agrave;i của quần đi k&egrave;m n&ecirc;n để ngắn đến 2 đường d&acirc;y buộc gi&agrave;y từ tr&ecirc;n xuống v&igrave; nếu d&agrave;i hơn, ống quần sẽ gẫy v&agrave; kh&ocirc;ng đẹp mắt.</p>\r\n\r\n<p>H&atilde;y &aacute;p dụng những tips tr&ecirc;n c&ugrave;ng những định hướng về phong c&aacute;ch vest &Yacute; bạn sẽ trở th&agrave;nh một qu&yacute; &ocirc;ng cực kỳ phong c&aacute;ch v&agrave; đẳng cấp.</p>\r\n', '15434134451543413445tin-01.jpg', 0),
(8, 1, 'NHỮNG ĐIỀU CẦN LƯU Ý KHI MUA VEST NAM', '<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hiện nay, vest nam đang bị 3 phong c&aacute;ch ch&iacute;nh chi phối l&agrave; phong c&aacute;ch Mỹ, Anh v&agrave; &Yacute;. Tuy nhi&ecirc;n, hiện nay xu hướng vest nam H&agrave;n Quốc cũng được nhiều qu&yacute; &ocirc;ng kh&aacute; ưa th&iacute;ch.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>\r\n', '<p><strong>Bạn&nbsp;c&oacute; thể &aacute;p dụng một số tips sau khi mua Vest Nam:</strong></p>\r\n\r\n<p><strong>Bước 1: Định h&igrave;nh phong c&aacute;ch</strong></p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/11.JPG\" style=\"height:530px; width:353px\" /></p>\r\n\r\n<p><em>Vest nam theo phong c&aacute;ch &Yacute; đang g&acirc;y n&ecirc;n một cơn sốt lớn</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện nay, vest nam đang bị 3 phong c&aacute;ch ch&iacute;nh chi phối l&agrave; phong c&aacute;ch Mỹ, Anh v&agrave; &Yacute;. Tuy nhi&ecirc;n, hiện nay xu hướng vest nam H&agrave;n Quốc cũng được nhiều qu&yacute; &ocirc;ng kh&aacute; ưa th&iacute;ch bởi sự lịch l&atilde;m kết hợp c&ugrave;ng sự l&atilde;ng tử của n&oacute;.</p>\r\n\r\n<p>Việc định h&igrave;nh phong c&aacute;ch gi&uacute;p bạn t&igrave;m ra được loại vest n&agrave;o sẽ ph&ugrave; hợp với m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 2: Ch&uacute; &yacute; về m&agrave;u sắc</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://www.elleman.vn/wp-content/uploads/2015/10/08/intero_2.jpg\"><img alt=\"\" src=\"https://biluxury.vn/media/Capture_1.JPG\" style=\"height:528px; width:353px\" /></a></p>\r\n\r\n<p><em>Nếu bạn c&oacute; nước da s&aacute;ng th&igrave; t&ocirc;ng m&agrave;u nổi l&agrave; lựa chọn h&agrave;ng đầu</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;u sắc của vest l&agrave; cực kỳ quan trọng. Những qu&yacute; &ocirc;ng y&ecirc;u th&iacute;ch sự đơn giản v&agrave; thuần khiết th&igrave; vest đen l&agrave; chuẩn nhất mọi thời đại.</p>\r\n\r\n<p>Nếu bạn c&oacute; l&agrave;n da s&aacute;ng th&igrave; vest m&agrave;u xanh c&ocirc;-ban hoặc xanh mi-nơ l&agrave; lựa chọn ho&agrave;n hảo.&nbsp;Nếu l&agrave;n da sậm th&igrave; m&agrave;u n&acirc;u nhạt, be cũng rất đ&aacute;ng để thử.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 3: Ch&uacute; &yacute; k&iacute;ch thước</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một bộ vest nam đẹp đ&uacute;ng chuẩn cần phải vừa vặn với th&acirc;n h&igrave;nh v&agrave; t&ocirc;n l&ecirc;n v&oacute;c d&aacute;ng của bạn. V&igrave; vậy việc ch&uacute; &yacute; k&iacute;ch thước l&agrave; điều ti&ecirc;n quyết n&ecirc;n để &yacute;.</p>\r\n\r\n<p>Khi nh&igrave;n v&agrave;o một bộ vest, bao giờ người ta cũng nh&igrave;n cầu vai đầu ti&ecirc;n, ch&iacute;nh v&igrave; thế cầu vai &aacute;o vest phải vừa vặn với tư thế v&agrave; t&ocirc;n l&ecirc;n d&aacute;ng điệu của người mặc. Một y&ecirc;u cầu kh&aacute;c nữa của chiếc &aacute;o vest chuẩn l&agrave; miếng đệm vai kh&ocirc;ng được trễ xuống hay tạo vết l&otilde;m tr&ecirc;n vai m&agrave; cần phải vừa vặn tr&ecirc;n vai bạn. Vai &aacute;o cũng phải &eacute;p chặt v&agrave; kh&ocirc;ng được thừa ra.</p>\r\n\r\n<p>Qu&yacute; &ocirc;ng n&agrave;o cũng c&oacute; &iacute;t nhất một bộ vest trong tủ đồ, c&aacute;c ng&ocirc;i sao của ch&uacute;ng ta thậm ch&iacute; c&oacute; cả t&aacute;, tuy nhi&ecirc;n, h&atilde;y xem sao n&agrave;o mới l&agrave; người mặc suit...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 4: Ch&uacute; &yacute; c&aacute;c chi tiết</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/Capture_2.JPG\" style=\"height:347px; width:357px\" /></p>\r\n\r\n<p><em>C&aacute;c chi tiết như vai v&agrave; cổ &aacute;o cũng rất quan trọng</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cổ &aacute;o cũng l&agrave; điểm cần lưu &yacute; khi chọn vest. Cổ &aacute;o phải &ocirc;m s&aacute;t g&aacute;y kết hợp với cổ &aacute;o sơmi đi k&egrave;m cao hơn khoảng 1cm.</p>\r\n\r\n<p>Phần tay của bộ vest phải phẳng phiu,&nbsp;mộng vai tr&ograve;n đều, kh&ocirc;ng nhăn nh&uacute;m. Khi chọn &aacute;o vest, c&aacute;c ch&agrave;ng n&ecirc;n để &yacute; chiều d&agrave;i của &aacute;o. Chiều d&agrave;i chuẩn cho một chiếc &aacute;o vest lịch sự l&agrave; tay c&oacute; thể chạm v&agrave;o đu&ocirc;i &aacute;o khi duỗi thẳng. Với những kiểu vest kho&aacute;c m&ugrave;a H&egrave; hoặc ph&aacute; c&aacute;ch để trẻ trung hơn th&igrave; c&oacute; thể thu ngắn chiều d&agrave;i &aacute;o lại.</p>\r\n\r\n<p>Một bộ vest nam đẹp th&igrave; chiều d&agrave;i cổ tay &aacute;o sơmi trong n&ecirc;n lộ ra so với chiều d&agrave;i tay &aacute;o comple khoảng 1 đến 2cm. C&ograve;n chiều d&agrave;i của quần đi k&egrave;m n&ecirc;n để ngắn đến 2 đường d&acirc;y buộc gi&agrave;y từ tr&ecirc;n xuống v&igrave; nếu d&agrave;i hơn, ống quần sẽ g&atilde;y v&agrave; kh&ocirc;ng đẹp mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 5: Kết hợp phụ kiện đi k&egrave;m</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/%C2%A6%2B%2B%C2%A6-008.JPG\" style=\"height:548px; width:576px\" /></p>\r\n\r\n<p><em>Bộ&nbsp;phụ kiện (nơ, khăn, c&agrave; vạt) đi k&egrave;m kh&ocirc;ng thể thiếu</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc sử dụng một chiếc caravat&nbsp;để nhấn nh&aacute; cho bộ vest th&ecirc;m hấp dẫn l&agrave; c&aacute;ch m&agrave; c&aacute;c qu&yacute; &ocirc;ng hay l&agrave;m. Bạn nhớ ch&uacute; &yacute; m&agrave;u sắc của vest để lựa chọn caravat cho ph&ugrave; hợp. Đ&ocirc;i khi, c&aacute;c qu&yacute; &ocirc;ng n&ecirc;n ph&aacute; c&aacute;ch bằng c&aacute;ch sử sụng khăn họa tiết gấp th&agrave;nh h&igrave;nh tam gi&aacute;c v&agrave; nh&eacute;t v&agrave;o t&uacute;i &aacute;o vest.</p>\r\n\r\n<p>Chỉ cần &aacute;p dụng 5 bước tr&ecirc;n l&agrave; bạn c&oacute; thể tự tin mua vest nam chuẩn kh&ocirc;ng cần chỉnh.</p>\r\n', '15434136581543413658tin-02.png', 1),
(9, 1, 'XUỐNG PHỐ CÙNG QUẦN SHORT KẾT HỢP VỚI ÁO SƠ MI - TẠI SAO KHÔNG?', '<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Nếu bạn coi &aacute;o sơ mi chỉ ph&ugrave; hợp với quần Jean hay quần &acirc;u th&igrave; nay Fashion2&nbsp; cho bạn 1 gợi &yacute; mặc đẹp với từ quần short với &aacute;o sơ mi.</span></span></span></p>\r\n', '<p><strong>1. TRẺ TRUNG HƠN NHỜ &Aacute;O SƠ MI CỘC TAY &amp; QUẦN SHORT</strong></p>\r\n\r\n<p>Đ&acirc;y sẽ l&agrave; một lựa chọn th&ocirc;ng minh khi bạn c&oacute; buổi hẹn với v&agrave;o những ng&agrave;y cuối tuần hoặc những &nbsp;buổi đi d&atilde; ngoại, set đồ n&agrave;y vừa khiến người mặc thoải m&aacute;i m&agrave; vẫn tạo ra được thiện cảm với người đối diện.</p>\r\n\r\n<p><strong>2.LỊCH SỰ, ẤN TƯỢNG HƠN NHỜ &Aacute;O SƠ MI D&Agrave;I TAY &amp; QUẦN SHORT</strong></p>\r\n\r\n<p>Nếu như sơ mi d&agrave;i tay v&agrave; quần &acirc;u l&agrave; &ldquo;cặp đ&ocirc;i ho&agrave;n hảo&rdquo; cho mọi ch&agrave;ng trai th&igrave; nay sao bạn kh&ocirc;ng thử ph&aacute; c&aacute;ch khi kết hợp &aacute;o sơ mi d&agrave;i tay c&ugrave;ng quần short?</p>\r\n\r\n<p>Với set đồ n&agrave;y, Fashion2&nbsp;tin chắc rằng d&ugrave; xuất hiện ở bất cứ nơi đ&acirc;u, bạn cũng vẫn lu&ocirc;n nổi bật với h&igrave;nh ảnh lịch sự nhưng cũng kh&ocirc;ng k&eacute;m phần trẻ trung.</p>\r\n\r\n<p>Nếu như bạn muốn nổi bật hơn, h&atilde;y chọn chiếc quần short nam họa tiết v&agrave; h&atilde;y kết hợp ch&uacute;ng c&ugrave;ng sơ mi trơn để kh&ocirc;ng g&acirc;y rối mắt bạn nh&eacute;!</p>\r\n\r\n<p>B&ecirc;n cạnh kiểu d&aacute;ng trang phục, bạn h&atilde;y ch&uacute; &yacute; chọn trang phục h&agrave;i h&ograve;a với l&agrave;n da của m&igrave;nh, tốt nhất n&ecirc;n chọn quần v&agrave; &aacute;o c&oacute; m&agrave;u sắc ton-sur-ton với nhau. Nguy&ecirc;n tắc phối &aacute;o sơ mi với quần short nam l&agrave; kh&ocirc;ng n&ecirc;n sử dụng qu&aacute; 3 m&agrave;u sắc trong 1 set đồ.</p>\r\n\r\n<p>Set đồ &aacute;o sơ mi phối hợp với quần short vốn dĩ đ&atilde; thể hiện c&aacute; t&iacute;nh năng động, ph&oacute;ng kho&aacute;ng của người mặc. Tuy nhi&ecirc;n, với việc mix th&ecirc;m một v&agrave;i m&oacute;n phụ kiện tinh tế, bạn sẽ trở n&ecirc;n nổi bật hơn rất nhiều. Đơn giản lắm, đ&oacute; c&oacute; thể l&agrave; một đ&ocirc;i sneaker &ldquo;chất lừ&rdquo;, đồng hồ thể thao hoặc thắt lưng,...tuy nhỏ nhắn, nhưng chắc chắn ch&uacute;ng sẽ khiến cho tổng thể trang phục của bạn trở n&ecirc;n ho&agrave;n hảo hơn đấy!</p>\r\n', '15434140681543414068tin-03.jpg', 0),
(10, 1, 'NHỮNG TIẾT LỘ THÚ VỊ VỀ MÀU SẮC SƠ MI KHÔNG PHẢI AI CŨNG BIẾT', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">&Aacute;o sơ mi&nbsp;l&agrave; vật phẩm kh&ocirc;ng thể thiếu. Chỉ cần tiết lộ m&agrave;u sắc chiếc &aacute;o sơ mi y&ecirc;u th&iacute;ch, ch&uacute;ng t&ocirc;i&nbsp;sẽ bật m&iacute; t&iacute;nh c&aacute;ch của bạn như thế n&agrave;o. </span></span></span></p>\r\n', '<p><strong>1. Xanh t&iacute;m than:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/ghep-04_1.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p>&nbsp;<em>M&agrave;u xanh t&iacute;m than &ndash; Ch&agrave;ng trai mạnh mẽ</em></p>\r\n\r\n<p>Đ&acirc;y l&agrave; tu&yacute;p người mạnh mẽ, c&aacute; t&iacute;nh v&agrave; độc lập. M&agrave;u xanh t&iacute;m than tr&ecirc;n chiếc &aacute;o sơ mi c&ograve;n cho thấy bạn tự chủ trong c&ocirc;ng việc v&agrave; cuộc sống. Kh&ocirc;ng dễ bị t&aacute;c động bởi những thứ xung quanh. Tuy nhi&ecirc;n đ&ocirc;i khi bạn cũng hay bị hiểu lầm l&agrave; qu&aacute; t&iacute;nh to&aacute;n trong cuộc sống.</p>\r\n\r\n<p>2.&nbsp;<strong>Đen:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/ghep-07_1.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>M&agrave;u đen &ndash; Ch&agrave;ng trai quyết đo&aacute;n</em></p>\r\n\r\n<p>C&aacute; t&iacute;nh nổi bật của nh&oacute;m m&agrave;u n&agrave;y l&agrave; độc lập v&agrave; quyết đo&aacute;n. B&ecirc;n cạnh đ&oacute;, ch&agrave;ng cũng rất kh&oacute; mở l&ograve;ng v&agrave; kh&aacute; b&iacute; ẩn, nội t&acirc;m. Điềm đạm, trầm t&iacute;nh v&agrave; ch&iacute;n chắn ch&iacute;nh cũng l&agrave; những điểm nổi bật thu h&uacute;t mọi người của ch&agrave;ng trai sơ mi đen.<strong>&nbsp;</strong></p>\r\n\r\n<p><strong>&nbsp;3.&nbsp;</strong><strong>T&iacute;m:</strong></p>\r\n\r\n<p>&nbsp;&nbsp;<img alt=\"\" src=\"https://biluxury.vn/media/ghep-01_1.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>M&agrave;u t&iacute;m &ndash; Ch&agrave;ng trai tinh tế</em>&nbsp;</p>\r\n\r\n<p>M&agrave;u t&iacute;m mang &yacute; nghĩa tinh tế, gi&agrave;u c&oacute; v&agrave; sang trọng. M&agrave;u t&iacute;m trong trang phục c&ograve;n thể hiện sự s&aacute;ng tạo, s&acirc;u sắc v&agrave; t&igrave;nh y&ecirc;u nghệ thuật. Đ&acirc;y cũng l&agrave; tu&yacute;p người l&atilde;ng mạn, c&oacute; đam m&ecirc; v&agrave; chung thủy trong t&igrave;nh y&ecirc;u.&nbsp;<strong>&nbsp;</strong></p>\r\n\r\n<p>&nbsp;<strong>4.</strong>&nbsp;<strong>Trắng:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/ghep-06_1.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>M&agrave;u trắng &ndash; Ch&agrave;ng trai l&atilde;ng tử</em></p>\r\n\r\n<p>X&eacute;t về kh&iacute;a cạnh t&acirc;m l&yacute;, người đ&agrave;n &ocirc;ng mặc đồ trắng gợi l&ecirc;n cảm gi&aacute;c vừa l&atilde;ng mạn vừa cầu to&agrave;n. Ngo&agrave;i vẻ đẹp chỉn chu chiếc &aacute;o sơ mi trắng c&ograve;n cho thấy bạn l&agrave; 1 người đ&agrave;n &ocirc;ng t&acirc;m l&yacute; v&agrave; tinh tế trong cuộc sống.</p>\r\n\r\n<p><strong>&nbsp;5.&nbsp;Xanh da trời:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/ghep-02_1.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>M&agrave;u xanh da trời &ndash; Ch&agrave;ng trai th&acirc;n thiện</em></p>\r\n\r\n<p>Những người th&iacute;ch m&agrave;u xanh da trời thường rất dễ gần, l&agrave;m việc rất trung thực. Họ dễ d&agrave;ng gi&agrave;nh được thiện cảm của đồng nghiệp, th&iacute;ch sự b&igrave;nh y&ecirc;n trong cuộc sống. Họ kh&ocirc;ng th&iacute;ch ph&aacute; bỏ những th&oacute;i quen cũ trong cuộc sống.</p>\r\n\r\n<p><strong>6.</strong>&nbsp;<strong>Sọc caro &ndash; Ch&agrave;ng trai năng động:</strong></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://biluxury.vn/media/ghep-03_1.jpg\" style=\"height:500px; width:800px\" /></em></p>\r\n\r\n<p><em>Sơ mi họa tiết kẻ caro &ndash; Ch&agrave;ng trai năng động</em></p>\r\n\r\n<p>Những chiếc &aacute;o sơ mi sọc caro thường to&aacute;t l&ecirc;n vẻ nam t&iacute;nh của người mặc. Họa tiết caro &ldquo;Gingham&rdquo; l&agrave; những đường kẻ &ocirc; vu&ocirc;ng đồng nhất dễ d&agrave;ng &ldquo;chinh phục&rdquo; những ch&agrave;ng trao c&oacute; c&aacute; t&iacute;nh năng động v&agrave; hiện đại.</p>\r\n\r\n<p><strong>7. Sọc dọc &ndash; Ch&agrave;ng trai thanh lịch:</strong></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://biluxury.vn/media/ghep-05_1.jpg\" style=\"height:500px; width:800px\" /></em></p>\r\n\r\n<p><em>Sơ mi họa tiết sọc dọc &ndash; Ch&agrave;ng trai&nbsp;thanh lịch</em></p>\r\n\r\n<p>Những ch&agrave;ng trai th&iacute;ch mặc &aacute;o sơ mi sọc dọc thường kh&oacute; nắm bắt. Họ kh&ocirc;ng để lộ t&igrave;nh cảm của m&igrave;nh cho người kh&aacute;c thấy. Đ&acirc;y l&agrave; tu&yacute;p người tinh tế, lịch thiệp.</p>\r\n\r\n<p>Nếu như trước đ&acirc;y bạn chỉ thầm nghĩ &ldquo;chọn đại 1 m&agrave;u m&igrave;nh y&ecirc;u th&iacute;ch&rdquo; th&igrave; b&acirc;y giờ, bạn đ&atilde; biết việc lựa chọn đ&oacute; c&oacute; sự li&ecirc;n quan rất gần đến t&iacute;nh c&aacute;ch của bạn rồi đấy!&nbsp;Qua ngay hệ thống Biluxury gần nhất v&agrave; chỉ cần n&oacute;i về t&iacute;nh c&aacute;ch của bạn, ch&uacute;ng t&ocirc;i sẽ chọn cho bạn mẫu sơ mi ph&ugrave; hợp nhất.</p>\r\n', '15434142691543414269tin-04.jpg', 1),
(11, 1, 'BÍ QUYẾT CHỌN QUẦN SHORT NAM CHO CHÀNG TRAI CÓ CHIỀU CAO KHIÊM TỐN', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Nếu như chiều cao l&agrave; r&agrave;o cản khiến c&aacute;c ch&agrave;ng trai &ldquo;ch&acirc;n ngắn&rdquo; cảm thấy kh&oacute; khăn trong việc lựa chọn quần &acirc;u hay quần jeans th&igrave; để ch&uacute;ng t&ocirc;i gợi &yacute; nh&eacute;!</span></span></span></p>\r\n', '<p>Đ&atilde; đến l&uacute;c bỏ qua sự tự ti về chiều cao để ch&agrave;o đ&oacute;n những chiếc quần short trong h&egrave; n&agrave;y. Cho d&ugrave; chỉ l&agrave; một buổi dạo quanh c&ocirc;ng vi&ecirc;n c&ugrave;ng với bạn b&egrave; hay một bữa ăn trưa th&acirc;n t&igrave;nh với đồng nghiệp, bạn sẽ lu&ocirc;n xuất hiện một c&aacute;ch đầy ấn tượng đấy!<a href=\"https://biluxury.vn/\">&nbsp;</a>Ch&uacute;ng t&ocirc;i tin rằng với những&nbsp;gợi &yacute;&nbsp;khi lựa chọn quần short dưới đ&acirc;y sẽ gi&uacute;p c&aacute;c ch&agrave;ng trai &ldquo;ch&acirc;n ngắn&rdquo; th&ecirc;m tự tin khi dạo bước xuống phố.&nbsp;</p>\r\n\r\n<p><strong>1. CHỌN CHIẾC QUẦN SHORT TR&Ecirc;N ĐẦU GỐI</strong></p>\r\n\r\n<p>Đ&acirc;y được xem l&agrave; ti&ecirc;u ch&iacute; số 1 m&agrave; bạn n&ecirc;n ch&uacute; &yacute; khi lựa chọn quần short, h&atilde;y đảm bảo rằng chiều d&agrave;i ống quần lu&ocirc;n ở tr&ecirc;n đầu gối 1 gấu quần. Tuy nhi&ecirc;n cũng đừng bao giờ chọn chiếc quần qu&aacute; ngắn bởi n&oacute; sẽ khiến bạn lộ bắp đ&ugrave;i v&agrave; hiển nhi&ecirc;n mọi người chỉ ch&uacute; &yacute; đến phần &ldquo;ch&acirc;n ngắn&rdquo; của bạn!</p>\r\n\r\n<p>Th&ecirc;m 1 lưu &yacute; nữa khi bạn lựa chọn quần short đ&oacute; l&agrave; h&atilde;y chọn mẫu quần short sắn gấu, v&agrave; đ&acirc;y được xem l&agrave; items chưa bao giờ lỗi mốt. H&atilde;y kết hợp quần ngố c&ugrave;ng &aacute;o ph&ocirc;ng kh&ocirc;ng cổ, vừa năng động m&agrave; vẫn rất &ldquo;men lỳ&rdquo; đấy chứ?</p>\r\n\r\n<p><strong>2. H&Atilde;Y CH&Uacute; &Yacute; ĐẾN M&Agrave;U DA CỦA M&Igrave;NH ĐỂ LỰA CHỌN M&Agrave;U QUẦN</strong></p>\r\n\r\n<p>Kh&ocirc;ng chỉ nữ giới, m&agrave; việc lựa chọn đồ cho nam giới cũng cực k&igrave; kh&oacute; khăn đấy! Họ cũng cần xem sự ph&ugrave; hợp của bộ đồ với l&agrave;n da của m&igrave;nh nữa, nhất l&agrave; những ch&agrave;ng trai c&oacute; chiều cao khi&ecirc;m tốn, họ lu&ocirc;n muốn tạo sự ấn tượng qua style ăn mặc của m&igrave;nh.</p>\r\n\r\n<p>Những items quần short với c&aacute;c m&agrave;u sắc trung t&iacute;nh như : đen, n&acirc;u, xanh navy, kem, n&acirc;u, x&aacute;m &hellip; sẽ l&agrave; lựa chọn an to&agrave;n, đừng mạo hiểm với những m&agrave;u sắc qu&aacute; sặc sỡ v&igrave; điều đ&oacute; sẽ g&acirc;y sự ch&uacute; &yacute; của mọi người đến trang phục bạn đang mặc v&agrave;&nbsp;<a href=\"https://biluxury.vn/\">Biluxury</a>&nbsp;tin rằng bạn kh&ocirc;ng muốn người kh&aacute;c nhớ đến m&igrave;nh với style &ldquo;một mẩu v&agrave; m&agrave;u m&egrave;&rdquo;!</p>\r\n\r\n<p><strong>3. CHỌN CHIẾC QUẦN TẠO CẢM GI&Aacute;C THOẢI M&Aacute;I, DỄ CHỊU</strong></p>\r\n\r\n<p>Ngo&agrave;i 2 yếu tố về m&agrave;u sắc v&agrave; độ d&agrave;i quần, bạn n&ecirc;n ch&uacute; &yacute; chọn chiếc quần c&oacute; chất vải thấm mồ h&ocirc;i tốt v&agrave; kh&ocirc;ng bị nhăn khi ch&uacute;ng ta hoạt động ngo&agrave;i trời nh&eacute;!</p>\r\n', '15434145181543414518tin-05.jpg', 1),
(12, 1, '5 SAI LẦM TRONG PHONG CÁCH ĂN MẶC SẼ KHIẾN BẠN TRỞ THÀNH THẢM HỌA THỜI TRANG', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Nếu như tủ đồ của nữ giới đa dạng với c&aacute;c set thời trang cầu k&igrave; th&igrave; thời trang nam lại thi&ecirc;n về sự đơn giản v&agrave; mạnh mẽ. </span></span></span></p>\r\n', '<p dir=\"ltr\"><strong>1. &ldquo;THAM LAM&rdquo; TRONG VIỆC TẠO ĐIỂM NHẤN BẰNG SUSPENDER (d&acirc;y đeo vai) KẾT HỢP C&Ugrave;NG THẮT LƯNG</strong></p>\r\n\r\n<p dir=\"ltr\">Nếu bạn mix cả 2 lại với nhau tr&ecirc;n 1 bộ trang phục sẽ khiến người đối diện cảm thấy rối mắt v&agrave; kh&ocirc;ng c&oacute; sự ăn nhập giữa c&aacute;c phụ kiện. H&atilde;y nhớ kĩ, chỉ được chọn 1 trong 2 phụ kiện đi k&egrave;m để c&oacute; thể trở n&ecirc;n nổi bật hơn nh&eacute;!</p>\r\n\r\n<p dir=\"ltr\"><strong>2. SỬ DỤNG THẮT LƯNG &nbsp;&ldquo;HẦM HỐ&rdquo;</strong></p>\r\n\r\n<p dir=\"ltr\">Nếu bạn để &yacute; kĩ sẽ thấy, thắt lưng c&oacute; h&igrave;nh th&ugrave; k&igrave; dị hoặc c&oacute; họa tiết hầm hố chỉ ph&ugrave; hợp với những show diễn thời trang. Nếu muốn trở th&agrave;nh người đ&agrave;n &ocirc;ng lịch l&atilde;m, sang trọng th&igrave; bạn n&ecirc;n chọn thắt lưng đơn giản, tối m&agrave;u th&ocirc;i c&aacute;c anh ch&agrave;ng nh&eacute;!</p>\r\n\r\n<p dir=\"ltr\"><strong>3. MẶC &Aacute;O CỔ CHỮ V</strong></p>\r\n\r\n<p dir=\"ltr\">Kiểu &aacute;o cổ chữ V chỉ hợp với style của nữ giới bởi n&oacute; sẽ ph&ocirc; được c&aacute;c ưu điểm tr&ecirc;n cơ thể của họ. C&ograve;n nam giới th&igrave; HO&Agrave;N TO&Agrave;N KH&Ocirc;NG!!! Bạn sẽ trở n&ecirc;n thật l&ocirc;i th&ocirc;i v&agrave; c&oacute; ch&uacute;t g&igrave; đ&oacute; k&eacute;m mạnh mẽ với chiếc &aacute;o như thế. Bạn n&ecirc;n lựa chọn chiếc &aacute;o ph&ocirc;ng cổ tr&ograve;n v&agrave; c&oacute; d&aacute;ng slim fit để ph&ocirc; diễn được n&eacute;t mạnh mẽ, c&aacute; t&iacute;nh của ph&aacute;i mạnh.</p>\r\n\r\n<p dir=\"ltr\"><strong>4. MẶC QUẦN D&Aacute;NG &Ocirc;M S&Aacute;T</strong></p>\r\n\r\n<p dir=\"ltr\">Nếu như kiểu quần với d&aacute;ng &ocirc;m s&aacute;t được chị em phụ nữ lựa chọn th&igrave; kh&ocirc;ng đồng nghĩa với việc n&oacute; c&ugrave;ng ph&ugrave; hợp với đ&agrave;n &ocirc;ng. D&aacute;ng &ocirc;m s&aacute;t của những chiếc skinny, nhất l&agrave; khi được kết hợp với m&agrave;u sắc rực rỡ, sẽ tạo n&ecirc;n những thảm họa thời trang thực sự với c&aacute;nh m&agrave;y r&acirc;u. C&aacute;c bạn nam n&ecirc;n chọn kiểu quần jeans/ quần &acirc;u với form d&aacute;ng vừa vặn, kh&ocirc;ng bị b&oacute; ở ch&acirc;n.</p>\r\n\r\n<p dir=\"ltr\"><strong>5. CHO QU&Aacute; NHIỀU ĐỒ V&Agrave;O T&Uacute;I QUẦN</strong></p>\r\n\r\n<p dir=\"ltr\">Nam giới thường c&oacute; th&oacute;i quen cất v&iacute; tiền, ch&igrave;a kh&oacute;a, điện thoại... v&agrave;o t&uacute;i quần. Điều n&agrave;y khiến bộ trang phục lịch l&atilde;m trở n&ecirc;n lộn xộn v&igrave; chiếc t&uacute;i quần qu&aacute; to, h&atilde;y mang theo 1 chiếc t&uacute;i x&aacute;ch cho nam để bỏ đồ thay v&igrave; &ldquo;đựng cả thế giới&rdquo; trong chiếc t&uacute;i quần như thế c&aacute;c bạn nh&eacute;!</p>\r\n\r\n<p dir=\"ltr\"><strong>6. MẶC VEST NH&Igrave;N NHƯ&hellip; R&Egrave;M CỬA!!!</strong></p>\r\n\r\n<p dir=\"ltr\">Những bộ vest tr&ocirc;ng giống như tấm thảm hay r&egrave;m cửa đều kh&ocirc;ng được coi l&agrave; thời trang. Nếu ch&uacute;ng xuất hiện th&igrave; chắc chắn chỉ c&oacute; trong c&aacute;c show diễn thời trang v&agrave; chắc chắn chẳng mấy ai diện ch&uacute;ng ra đường cả!</p>\r\n', '15434146401543414640tin-06.jpg', 1),
(13, 1, '3 BÍ QUYẾT MẶC VEST ĐẸP CHO NGƯỜI GẦY  ', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Với những gợi &yacute; sau,&nbsp;những chàng trai g&acirc;̀y, có dáng mảnh khảnh sẽ cực kỳ y&ecirc;n t&acirc;m khi bi&ecirc;́t&nbsp;<u>bí quy&ecirc;́t mặc vest cho người g&acirc;̀y:</u></span></span></span></p>\r\n', '<p><strong>1. LỰA CHỌN VEST S&Aacute;NG M&Agrave;U</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://biluxury.vn/media/WEB%201.jpg\" style=\"height:500px; width:800px\" /></strong></p>\r\n\r\n<p>B&iacute; quyết đầu ti&ecirc;n để lựa chọn trang phục cho người gầy đ&oacute; l&agrave; h&atilde;y chọn tone m&agrave;u s&aacute;ng, đ&oacute; cũng l&agrave; ti&ecirc;u ch&iacute; quan trọng khi bạn lựa chọn bộ vest, bởi tone s&aacute;ng sẽ &ldquo;đ&aacute;nh lừa thị gi&aacute;c&rdquo; v&agrave; khiến bạn trở n&ecirc;n &ldquo;c&oacute; da, c&oacute; thịt hơn&rdquo;. Bạn c&oacute; thể lựa chọn c&aacute;c tone m&agrave;u &ldquo;an to&agrave;n&rdquo; như: m&agrave;u kem, m&agrave;u ghi, m&agrave;u xanh navy&hellip; Khi lựa chọn vest h&atilde;y ch&uacute; &yacute; c&acirc;n nhắc để bộ trang phục h&agrave;i h&ograve;a với m&agrave;u da của m&igrave;nh, đừng cố gắng &ldquo;l&agrave;m mới&rdquo; m&igrave;nh bằng những bộ đồ l&ograve;e loẹt hoặc họa tiết qu&aacute; &ldquo;kh&aacute;c người&rdquo; v&igrave; điều đ&oacute; sẽ khiến mọi người ấn tượng bạn bởi h&igrave;nh ảnh &ldquo;m&agrave;u m&egrave; v&agrave; một mẩu&rdquo; m&agrave; th&ocirc;i!</p>\r\n\r\n<p><strong>2. CH&Uacute; &Yacute; ĐẾN CHẤT LIỆU VẢI VEST</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://biluxury.vn/media/WEB2.jpg\" style=\"height:500px; width:800px\" /></strong></p>\r\n\r\n<p>Chất liệu vải được xem l&agrave; ti&ecirc;u ch&iacute; quan trọng để đ&aacute;nh gi&aacute; một bộ trang phục đẳng cấp, với những nam giới gầy, việc lựa chọn bộ vest c&oacute; chất liệu vải th&iacute;ch hợp sẽ gi&uacute;p cho họ che được c&aacute;c nhược điểm tr&ecirc;n cơ thể v&agrave; tạo được thiện cảm với người đối diện. V&igrave; vậy, khi lựa chọn vest,&nbsp;<a href=\"https://biluxury.vn/\">Biluxury</a>&nbsp;khuy&ecirc;n bạn n&ecirc;n ưu ti&ecirc;n chọn bộ vest được l&agrave;m từ chất liệu vải Tu&yacute;t si v&agrave; vải Wood (vải len) bởi v&igrave; ch&uacute;ng đều l&agrave; chất liệu h&agrave;ng đầu d&agrave;nh cho người gầy. Đ&oacute; l&agrave; hai loại vải nặng sẽ gi&uacute;p bạn dễ d&agrave;ng che đi khuyết điểm ở phần vai v&agrave; lồng ngực k&eacute;m đầy đặn.</p>\r\n\r\n<ul>\r\n	<li><strong>Vải Tu&yacute;t si</strong>&nbsp;c&oacute; đặc t&iacute;nh mềm, mịn, &iacute;t nhăn, đa dạng về m&agrave;u sắc v&agrave; mẫu m&atilde;. &Aacute;o vest được may từ loại vải n&agrave;y thường giữ được phom d&aacute;ng rất tốt v&agrave; c&oacute; độ co gi&atilde;n th&iacute;ch hợp đủ để tạo sự thoải m&aacute;i cho người mặc.</li>\r\n	<li><strong>Vải Wood</strong>&nbsp;(vải len) thường được l&agrave;m từ sợi l&ocirc;ng cừu n&ecirc;n rất nhẹ, tho&aacute;ng v&agrave; m&agrave;u sắc tươi s&aacute;ng. Kh&ocirc;ng chỉ &aacute;o vest, tất cả c&aacute;c trang phục được may từ loại chất liệu vải n&agrave;y để c&oacute; khả năng thấm h&uacute;t mồ h&ocirc;i tốt, thể hiện sự đẳng cấp của người mặc. Những bộ vest được l&agrave;m từ chất liệu vải n&agrave;y thường được may với form &ocirc;m vừa phải.</li>\r\n</ul>\r\n\r\n<p><strong>3. KIỂU D&Aacute;NG VEST</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://biluxury.vn/media/WEB3.jpg\" style=\"height:500px; width:800px\" /></strong></p>\r\n\r\n<p>Những người gầy thường c&oacute; th&oacute;i quen v&agrave; xu hướng lựa chọn bộ trang phục rộng r&atilde;i để khiến họ b&eacute;o hơn. Tuy nhi&ecirc;n đ&acirc;y sẽ l&agrave; sai lầm tai hại khi bạn coi đ&oacute; l&agrave; ti&ecirc;u ch&iacute; chọn vest. Nếu bạn chọn bộ vest rộng r&atilde;i th&igrave; nh&igrave;n bạn sẽ trở th&agrave;nh người &ldquo;bơi trong quần &aacute;o&rdquo; v&agrave; lọt thỏm trong bộ vest khổng lồ.&nbsp;</p>\r\n', '15434147521543414752tin-07.jpg', 1),
(14, 1, '8 quy tắc phối đồ nữ giới nên biết', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Nếu biết c&aacute;ch phối đồ chuẩn mực sẽ gi&uacute;p bạn g&aacute;i lu&ocirc;n xuất hiện thời thượng, thanh lịch v&agrave; xinh đẹp trong mắt mọi người.</span></span></p>\r\n', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>Dưới đ&acirc;y l&agrave; 8 quy tắc phối đồ nữ giới n&ecirc;n biết:</strong></span></p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 1\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_2.jpg\" style=\"height:373px; width:650px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Khi mặc &aacute;o sơ mi, bạn kh&ocirc;ng n&ecirc;n th&aacute;o bỏ qu&aacute; 2 n&uacute;t c&uacute;c.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 2\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_3.jpg\" style=\"height:783px; width:650px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kh&ocirc;ng n&ecirc;n đeo qu&aacute; nhiều trang sức c&ugrave;ng loại, 3-4 m&oacute;n đồ c&ugrave;ng l&uacute;c c&oacute; thể khiến bạn tr&ocirc;ng kh&ocirc;ng sang trọng.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 3\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_5.jpg\" style=\"height:510px; width:650px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đừng mặc v&aacute;y vừa ngắn lại vừa hở vai hoặc xẻ s&acirc;u ngực. Điều đ&oacute; c&oacute; thể khiến bạn tr&ocirc;ng th&ocirc; tục, kh&ocirc;ng lịch sự.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 4\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_7.jpg\" style=\"height:373px; width:650px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cổ &aacute;o c&ocirc;ng sở kh&ocirc;ng n&ecirc;n xẻ s&acirc;u qu&aacute; 10 cm t&iacute;nh từ xương đ&ograve;n.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 5\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_11.jpg\" style=\"height:373px; width:650px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kh&ocirc;ng kết hợp qu&aacute; nhiều họa tiết trong c&ugrave;ng một trang phục. Nếu th&iacute;ch, h&atilde;y chọn c&aacute;c họa tiết c&ugrave;ng m&agrave;u hoặc với k&iacute;ch thước kh&aacute;c nhau.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 6\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_13.jpg\" style=\"height:389px; width:650px\" title=\"8 quy tắc phối đồ nữ giới nên biết hình ảnh 6 \" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nếu mặc &aacute;o kh&ocirc;ng tay, h&atilde;y sử dụng kiểu &aacute;o che k&iacute;n vai, đặc biệt l&agrave; d&acirc;n văn ph&ograve;ng. &Aacute;o 2 d&acirc;y kh&ocirc;ng mang lại phong c&aacute;ch thanh lịch cho bạn g&aacute;i.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 7\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_14.jpg\" style=\"height:500px; width:650px\" title=\"8 quy tắc phối đồ nữ giới nên biết hình ảnh 7 \" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kh&ocirc;ng n&ecirc;n mặc những loại đồ l&oacute;t d&agrave;y hoặc c&oacute; bề mặt gồ ghề dưới quần k&iacute;n hoặc những chiếc v&aacute;y ngắn m&agrave;u sặc sỡ. N&oacute; sẽ l&agrave;m vải bị sần l&ecirc;n v&agrave; mọi người c&oacute; thể nh&igrave;n thấy r&otilde; r&agrave;ng &aacute;o ngực của bạn.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 quy tac phoi do nu gioi nen biet hinh anh 8\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/cqxrcajwp/2018_02_01/thoi_trang_15.jpg\" style=\"height:436px; width:650px\" title=\"8 quy tắc phối đồ nữ giới nên biết hình ảnh 8 \" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sử dụng băng d&iacute;nh 2 mặt cho phần c&uacute;c &aacute;o sơ mi để tr&aacute;nh bị hở &aacute;o ngực.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '15434153491543415349tin-08.jpg', 0),
(15, 1, '5 TIPS ĐỂ PHỤ NỮ TUỔI 35 MẶC ĐẸP HƠN CẢ THỜI ĐÔI MƯƠI', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">H&atilde;y đẹp theo c&aacute;ch ri&ecirc;ng của bạn bằng c&aacute;ch lựa chọn những bộ c&aacute;nh &ldquo;quyền lực&rdquo; t&ocirc;n vinh sức hấp dẫn của phụ nữ tuổi 35.</span></span></p>\r\n', '<p>Dưới đ&acirc;y l&agrave; một số mẹo hữu &iacute;ch v&agrave; dễ &aacute;p dụng gi&uacute;p bạn c&oacute; vẻ ngo&agrave;i cuốn h&uacute;t, thanh lịch ở độ tuổi tr&ecirc;n 35.</p>\r\n\r\n<h3>1. Cần phải c&oacute; &iacute;t nhất một chiếc v&aacute;y b&uacute;t ch&igrave; trong tủ</h3>\r\n\r\n<p>Kiểu v&aacute;y n&agrave;y l&agrave; một trong những lựa chọn tốt nhất d&agrave;nh cho phụ nữ tuổi 35 khi cơ thể đang dần k&eacute;m săn chắc, v&ugrave;ng eo t&iacute;ch nhiều mỡ. V&aacute;y b&uacute;t ch&igrave; sẽ gi&uacute;p &ldquo;thắt chặt&rdquo; c&aacute;c ngấn mỡ đồng thời gi&uacute;p cơ thể tr&ocirc;ng d&agrave;i hơn.</p>\r\n\r\n<p>Tốt nhất, bạn n&ecirc;n chọn kiểu v&aacute;y cạp cao v&agrave; c&oacute; độ d&agrave;i tới gối. C&oacute; thể thoải m&aacute;i lựa chọn m&agrave;u sắc nhưng n&ecirc;n tr&aacute;nh xa t&ocirc;ng m&agrave;u neon nh&eacute;.</p>\r\n\r\n<h3>2. V&aacute;y đen (LBD) l&agrave; m&oacute;n đồ thể hiện quyền lực</h3>\r\n\r\n<p>V&aacute;y đen l&agrave; m&oacute;n đồ thể hiện quyền năng của phụ nữ tuổi 35: Thanh lịch, từng trải v&agrave; đầy sự sang trọng. Bạn n&ecirc;n chọn v&aacute;y đen c&oacute; thiết kế đơn giản, &iacute;t rườm r&agrave;.</p>\r\n\r\n<p>C&oacute; thể chọn v&aacute;y đen phối hợp với họa tiết ren, hay chấm bi xinh xắn để tạo điểm nhấn v&agrave; mix c&ugrave;ng đ&ocirc;i gi&agrave;y cao g&oacute;t đơn giản l&agrave; bạn đ&atilde; c&oacute; vẻ ngo&agrave;i thật ấn tượng v&agrave; s&agrave;nh điệu rồi đấy.</p>\r\n\r\n<p><em>Đầm đen phối họa tiết chấm bi S104 / Gi&aacute; gốc: 420.000 VND off 30% &ndash; Gi&aacute; sale: 294.000 VND</em></p>\r\n\r\n<h3>3. Gam m&agrave;u pastel t&ocirc;n vinh vẻ ngọt ng&agrave;o</h3>\r\n\r\n<p>C&agrave;ng c&oacute; tuổi phụ nữ c&agrave;ng bị cuốn h&uacute;t bởi những g&igrave; nh&atilde; nhặn, nhẹ nh&agrave;ng v&agrave; gam m&agrave;u pastel l&agrave; thứ hội tụ đủ sự tinh tế v&agrave; vẻ đẹp thời trang.</p>\r\n\r\n<p><em>Đầm c&ocirc;ng sở viền trắng thắt nơ eo KK68-50 / Gi&aacute; gốc: 400.000 VND off 15% &ndash; Gi&aacute; sale: 340.000 VND</em></p>\r\n\r\n<p>H&atilde;y kho&aacute;c l&ecirc;n m&igrave;nh những bộ c&aacute;nh pastel nền n&atilde; như trắng ng&agrave;, xanh, hồng, ghi&hellip; để vẻ mặn m&agrave; của phụ nữ tuổi 35 được dịp ph&aacute;t huy hết sức mạnh.</p>\r\n\r\n<p><em>Đầm x&ograve;e sắc hồng ngọt ng&agrave;o S10 / Gi&aacute; gốc 420.000 VND off 50% &ndash; Gi&aacute; sale 210.000 VND</em></p>\r\n\r\n<h3>4. Một đ&ocirc;i gi&agrave;y thoải m&aacute;i mang đến sự tự tin</h3>\r\n\r\n<p>Phụ nữ tuổi 35 c&oacute; lẽ c&oacute; qu&aacute; nhiều bận rộn để đi một đ&ocirc;i gi&agrave;y qu&aacute; cao hay trang tr&iacute; qu&aacute; cầu kỳ. Bạn n&ecirc;n ưu ti&ecirc;n những đ&ocirc;i gi&agrave;y nhẹ nh&agrave;ng, xinh xắn, kh&ocirc;ng qu&aacute; cao v&agrave; thiết kế c&agrave;ng đơn giản c&agrave;ng tốt.</p>\r\n\r\n<p><a href=\"https://kkfashion.vn/wp-content/uploads/2017/10/7-5-tip-de-phu-nu-tuoi-35-mac-dep-hon-ca-thoi-doi-muoi.jpg\"><img alt=\"5 tips để phụ nữ tuổi 35 mặc đẹp hơn cả thời đôi mươi\" src=\"https://kkfashion.vn/wp-content/uploads/2017/10/7-5-tip-de-phu-nu-tuoi-35-mac-dep-hon-ca-thoi-doi-muoi.jpg\" style=\"height:652px; width:564px\" /></a></p>\r\n', '15434156071543415607tin-09.png', 1),
(16, 2, 'ÁO KHOÁC DA: CÁCH TÂN KIỂU DÁNG, GHI ĐIỂM THỜI TRANG', '<p>Cột mốc đ&aacute;nh dấu sự trưởng th&agrave;nh về&nbsp;thời trang của c&aacute;c ch&agrave;ng trai&nbsp;c&oacute; lẽ từ l&acirc;u vẫn lu&ocirc;n được đ&aacute;nh gi&aacute; qua những chiếc &aacute;o kho&aacute;c da đầu ti&ecirc;n.&nbsp;</p>\r\n', '<p>Trải qua sự biến đổi nhiều thăng trầm&nbsp;từ đồng phục của &ldquo;d&acirc;n chơi&rdquo; motor những năm 50, cho tới xu thế thịnh h&agrave;nh của những nh&oacute;m nhạc Punk thập ni&ecirc;n 70, những chiếc &aacute;o kho&aacute;c da vẫn xứng đ&aacute;ng với ng&ocirc;i vương trang phục thời thượng nhất. Với tuổi đời kh&ocirc;ng nhỏ của m&igrave;nh, chiếc &aacute;o c&aacute; t&iacute;nh n&agrave;y&nbsp;nghiễm nhi&ecirc;n chiếm trọn cảm t&igrave;nh của đấng m&agrave;y r&acirc;u, từ những qu&yacute; &ocirc;ng trưởng th&agrave;nh, gi&agrave; dặn cho tới những g&atilde; trai trẻ ng&ocirc;ng ngh&ecirc;nh, ngạo nghễ với đời.</p>\r\n\r\n<p>Một trong những l&yacute; do khiến c&aacute;c ch&agrave;ng trai vẫn chần chừ trong việc quyết định mua những chiếc &aacute;o kho&aacute;c da đ&oacute; ch&iacute;nh l&agrave; kh&ocirc;ng biết mặc thế n&agrave;o cho đẹp. Việc &ldquo;mix-and-match&rdquo; với &aacute;o kho&aacute;c da cũng như một b&agrave;i kiểm tra về gu thẩm mỹ&nbsp;của bản th&acirc;n bạn.</p>\r\n\r\n<h3><strong>&Aacute;o kho&aacute;c da cổ điển: Đơn giản m&agrave; dễ mặc</strong></h3>\r\n\r\n<p>Trong lịch sử thời trang, sự&nbsp;cổ điển trong gu thẩm mỹ vẫn chưa bao giờ bị coi l&agrave; &ldquo;hết thời&rdquo;. Theo quan niệm th&ocirc;ng thường, đ&acirc;y được coi l&agrave; sự mẫu mực ti&ecirc;u biểu của những thời k&igrave; trước. Bởi vậy với những item thời thượng mang t&iacute;nh &ldquo;iconic&rdquo; cực lớn như &aacute;o kho&aacute;c da, đường n&eacute;t cổ điển ch&iacute;nh l&agrave; ti&ecirc;u ch&iacute; quan trọng trong lựa chọn&nbsp;&ldquo;must-have&rdquo; n&agrave;y của c&aacute;c ch&agrave;ng.</p>\r\n\r\n<p>&Aacute;o kho&aacute;c da được thừa hưởng sự tinh tế v&agrave; ho&agrave;n mỹ bậc nhất từ những thế hệ đầu ti&ecirc;n. C&oacute; lẽ v&igrave; thế m&agrave; c&aacute;ch kết hợp với ch&uacute;ng cũng kh&aacute; dễ d&agrave;ng, thoải m&aacute;i v&agrave; đơn giản. C&ugrave;ng chiếc &aacute;o ph&ocirc;ng trắng tương phản với &aacute;o da m&agrave;u đen, c&aacute;ch tốt nhất để kết hợp l&agrave; lựa chọn cho m&igrave;nh một chiếc quần chinos với phomg d&aacute;ng&nbsp;tương th&iacute;ch, tạo n&ecirc;n phong th&aacute;i đĩnh đạc m&agrave; kh&ocirc;ng k&eacute;m phần tinh tế. V&agrave; dĩ nhi&ecirc;n kh&ocirc;ng thể thiếu đi&nbsp;những đ&ocirc;i gi&agrave;y da n&acirc;u thật bụi bặm, c&aacute; t&iacute;nh.</p>\r\n\r\n<p>Với những ch&agrave;ng trai đ&atilde; trải qua nhiều thăng trầm trong cuộc đời, c&oacute; lẽ&nbsp;những chiếc&nbsp;&aacute;o kho&aacute;c nam&nbsp;&ldquo;chất chơi&rdquo; n&agrave;y sẽ ph&ugrave; hợp hơn với phong c&aacute;ch lịch sự của một qu&yacute; &ocirc;ng đ&uacute;ng chất! H&atilde;y lựa chọn cho m&igrave;nh một chiếc &aacute;o sơ mi với m&agrave;u sắc nhẹ nh&agrave;ng, nh&atilde; nhặn. Th&ecirc;m thắt một chiếc c&agrave; vạt v&agrave; chững chạc với chiếc quần &acirc;u m&agrave;u x&aacute;m vừa vặn. Chắc chắn, outfit n&agrave;y sẽ gi&uacute;p bạn trở n&ecirc;n nổi bật hơn trong mắt người đối diện với vẻ bề ngo&agrave;i mang đậm phong c&aacute;ch &ldquo;vintage&rdquo; của một qu&yacute; &ocirc;ng từng trải.</p>\r\n\r\n<h3><strong>H&atilde;y thử l&agrave;m mới phong c&aacute;ch bản th&acirc;n với chiếc &aacute;o kho&aacute;c da m&agrave;u đỏ đun.</strong></h3>\r\n\r\n<p>Điều đặc biệt ở những chiếc &aacute;o kho&aacute;c da đ&oacute; ch&iacute;nh l&agrave; chỉ cần kho&aacute;c ch&uacute;ng l&ecirc;n, bạn c&oacute; thể v&ocirc; tư kết hợp với bất kỳ bộ đồ n&agrave;o m&agrave; vẫn tr&ocirc;ng cực k&igrave; &ldquo;chất chơi&rdquo;. Bởi lẽ đ&oacute;, tại sao kh&ocirc;ng thử sức chơi đ&ugrave;a với những chiếc &aacute;o da c&oacute; m&agrave;u sắc th&uacute; vị hơn nhỉ?</p>\r\n\r\n<h3><strong>&Aacute;o kho&aacute;c da Biker, phong c&aacute;ch của ch&agrave;ng Bad Boy c&aacute; t&iacute;nh</strong></h3>\r\n\r\n<p>Thịnh h&agrave;nh từ những thập ni&ecirc;n 50, Biker Jacket được biết đến như &ldquo;người em trai nổi loạn&rdquo; của &aacute;o kho&aacute;c da cổ điển. Ch&uacute;ng mang đến cho người mặc phong c&aacute;ch&nbsp;đường phố độc đ&aacute;o v&agrave; &ldquo;chất chơi&rdquo; hơn hẳn người anh. Điểm cộng cho &aacute;o kho&aacute;c Biker l&agrave; c&aacute;ch kết hợp rất đơn giản v&agrave; kh&ocirc;ng qu&aacute; cầu k&igrave;. Tuy vậy, bạn cần phải ch&uacute; trọng hơn khi &ldquo;mix- match&rdquo; bởi Biker Jacket c&oacute; đặc th&ugrave; kiểu d&aacute;ng b&oacute; s&aacute;t, &ocirc;m người hơn d&aacute;ng &aacute;o cổ điển.</p>\r\n\r\n<h3><strong>&Aacute;o kho&aacute;c Aviator độc đ&aacute;o hay Bomber Jacket truyền thống?</strong></h3>\r\n\r\n<p>Nếu kể đến sự đa dạng về kiểu d&aacute;ng của những chiếc &aacute;o kho&aacute;c da, chắc chắn ta kh&ocirc;ng thể bỏ qua hai kiểu thời thượng nhất l&agrave; Aviator v&agrave; Bomber. C&ugrave;ng bắt nguồn từ&nbsp;nhu cầu giữ ấm trong qu&acirc;n đội nhưng c&oacute; lẽ, độ phổ biến của ch&uacute;ng c&oacute; lẽ đ&atilde; vượt mặt đ&agrave;n anh &aacute;o da cổ điển hay Biker Jacket.</p>\r\n\r\n<p>Lời khuy&ecirc;n cho c&aacute;c fan h&acirc;m mộ &aacute;o kho&aacute;c Aviator ch&iacute;nh l&agrave;: C&agrave;ng đơn giản c&agrave;ng tốt! Điều đ&oacute; đồng nghĩa rằng &aacute;o ph&ocirc;ng trắng vẫn sẽ ph&aacute;t huy hiệu quả. Tiếp theo, bạn chỉ cần phối ch&uacute;ng với một chiếc quần Cargo bụi bặm c&ugrave;ng một đ&ocirc;i ủng bạc m&agrave;u l&agrave; đ&atilde; sở hữu cho m&igrave;nh một bộ Outfit &ldquo;đỉnh của đỉnh&rdquo; rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '15434161751543416175tin-01.jpg', 1),
(17, 2, 'TRỞ THÀNH MỸ NAM CHỐN CÔNG SỞ VỚI BỘ ĐỒ VEST - HÃY “NẰM LÒNG” 5 NGUYÊN TẮC SAU', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&oacute; thể bạn chưa biết: Ấn tượng đầu ti&ecirc;n đến từ bộ trang phục sẽ tạo n&ecirc;n thiện cảm v&agrave; ấn tượng tốt cho người đối diện. </span></span></p>\r\n', '<p><strong>1. CHỌN VEST Đ&Uacute;NG SIZE</strong></p>\r\n\r\n<p>Nguy&ecirc;n tắc đầu ti&ecirc;n v&agrave; l&agrave; nguy&ecirc;n tắc quan trọng nhất đ&oacute; l&agrave; bạn phải lu&ocirc;n đảm bảo bộ trang phục của m&igrave;nh vừa vặn với cơ thể</p>\r\n\r\n<p><img src=\"https://biluxury.vn/media/webb1.jpg\" /></p>\r\n\r\n<p>Bạn đ&atilde; thử tưởng tượng nếu m&igrave;nh mặc bộ vest&nbsp;rộng th&ugrave;ng th&igrave;nh hoặc bộ vest qu&aacute; chật sẽ ra sao chưa? Nếu kh&ocirc;ng muốn tự biến m&igrave;nh th&agrave;nh &ldquo;thảm họa thời trang&rdquo; h&atilde;y coi đ&acirc;y l&agrave; ti&ecirc;u ch&iacute; đầu ti&ecirc;n khi chọn đồ.</p>\r\n\r\n<p><strong>2. MẶC TRANG PHỤC PH&Ugrave; HỢP VỚI M&Ocirc;I TRƯỜNG L&Agrave;M VIỆC</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://biluxury.vn/media/webb2.jpg\" style=\"height:450px; width:600px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đừng bao giờ tự &ldquo;l&agrave;m mới&rdquo; m&igrave;nh bằng việc tạo ra style &ldquo;kh&ocirc;ng giống ai&rdquo;. M&ocirc;i trường c&ocirc;ng sở lu&ocirc;n đ&ograve;i hỏi t&iacute;nh chỉnh chu, vậy n&ecirc;n những bộ vest c&oacute; gam m&agrave;u trung t&iacute;nh, &iacute;t họa tiết v&agrave; kh&ocirc;ng qu&aacute; kh&aacute;c biệt sẽ l&agrave; sự lựa chọn an to&agrave;n. Bạn cũng c&oacute; thể chọn những bộ vest s&aacute;ng m&agrave;u nhưng phải đảm bảo sự h&agrave;i h&ograve;a v&agrave; kh&ocirc;ng c&oacute; qu&aacute; nhiều họa tiết dị biệt, bởi nếu bạn để &yacute; kĩ sẽ thấy, bộ vest nhiều họa tiết sẽ xuất hiện ở tr&ecirc;n c&aacute;c s&agrave;n diễn thời trang nhiều hơn l&agrave; m&ocirc;i trường c&ocirc;ng sở.</p>\r\n\r\n<p><strong>3. LỰA CHỌN TẤT PH&Ugrave; HỢP VỚI TRANG PHỤC</strong></p>\r\n\r\n<p>Rất nhiều ch&agrave;ng trai thường mắc phải lỗi &ldquo;sơ đẳng&rdquo; n&agrave;y khi mặc đồ. V&agrave; v&ocirc; t&igrave;nh, d&ugrave; bạn mặc đồ c&oacute; đẹp đến đ&acirc;u nhưng chỉ v&igrave; chọn sai m&agrave;u tất m&agrave; sẽ khiến bạn bị &ldquo;trừ điểm&rdquo; trong mắt người đối diện.</p>\r\n\r\n<p><img src=\"https://biluxury.vn/media/webb6_1.jpg\" /></p>\r\n\r\n<p>Lời khuy&ecirc;n m&agrave; Biluxury đưa ra đ&oacute; l&agrave;: H&atilde;y ưu ti&ecirc;n chọn c&aacute;c m&agrave;u tất sẫm m&agrave;u, tone tối như: n&acirc;u, x&aacute;m hoặc đơn giản hơn l&agrave; h&atilde;y chọn một đ&ocirc;i tất c&ugrave;ng tone m&agrave;u với bộ vest m&agrave; bạn đang mặc. Như vậy, bộ quần &aacute;o của bạn sẽ h&agrave;i h&ograve;a về tổng thể v&agrave; kh&ocirc;ng khiến đồng nghiệp phải ch&uacute; &yacute; đến đ&ocirc;i ch&acirc;n của bạn.</p>\r\n', '15434163331543416333tin-02.jpg', 1),
(18, 2, '8 QUY TẮC CÀI NÚT ÁO VEST CHO QUÝ ÔNG BẠN NÊN BIẾT', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Suit nam l&agrave; thứ trang phục cổ xưa. Thoạt nghe, chắc hẳn nhiều người sẽ hơi ngạc nhi&ecirc;n v&igrave; những chiếc n&uacute;t th&igrave; cần g&igrave; đến những quy tắc! </span></span></p>\r\n', '<p>H&atilde;y ghi nhớ cẩn thận những quy tắc c&agrave;i n&uacute;t của từng phong c&aacute;ch suit m&agrave; b&agrave;i viết chia sẻ sau đ&acirc;y, bởi v&igrave; &ldquo;small details make big difference&rdquo; (tạm dịch: Những chi tiết nhỏ l&agrave;m n&ecirc;n sự kh&aacute;c biệt lớn)</p>\r\n\r\n<p><strong>1. Loại một n&uacute;t</strong></p>\r\n\r\n<p>Với kiểu &aacute;o n&agrave;y th&igrave; qu&aacute; đơn giản khi vốn dĩ chỉ c&oacute; một chiếc n&uacute;t. Nhưng trước hết bạn phải thuộc khẩu quyết &ldquo;đứng c&agrave;i, ngồi mở&rdquo;: khi đứng th&igrave; c&agrave;i n&uacute;t v&agrave; cởi n&uacute;t trước khi ngồi để giữ &aacute;o kh&ocirc;ng bị nhăn, quy tắc n&agrave;y &aacute;p dụng cho tất cả kiểu d&aacute;ng suit</p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/a1.JPG\" style=\"height:533px; width:357px\" /></p>\r\n\r\n<p><em>Vest 1 n&uacute;t</em></p>\r\n\r\n<p><strong>2. Loại hai n&uacute;t</strong></p>\r\n\r\n<p>Đối với suit hai n&uacute;t, c&agrave;i n&uacute;t tr&ecirc;n v&agrave; bỏ n&uacute;t dưới. Nguồn gốc v&igrave; sao kh&ocirc;ng được c&agrave;i chiếc n&uacute;t thứ hai như sau: Vua Edward Đệ Thất l&agrave; một người b&eacute;o tr&ograve;n, đến nỗi kh&ocirc;ng bao giờ bộ suit của &ocirc;ng c&oacute; thể g&agrave;i được chiếc n&uacute;t thứ hai, do đ&oacute; mọi người trong triều đ&igrave;nh lẫn d&acirc;n ch&uacute;ng cũng phải bắt chước theo nh&agrave; vua. Dần d&agrave; n&oacute; trở th&agrave;nh một quy ước bất th&agrave;nh văn cho bất cứ ai, mặc suit nam m&agrave; c&agrave;i n&uacute;t cuối c&ugrave;ng l&agrave; điều tối kỵ. Bởi v&igrave; l&agrave; quy ước n&ecirc;n bạn phải l&agrave;m theo, v&agrave; kh&ocirc;ng thắc mắc.</p>\r\n\r\n<p>V&agrave; ngo&agrave;i ra, suit hiện đại được thiết kế với chiếc n&uacute;t thứ hai chỉ để trang tr&iacute; v&agrave; phần eo v&aacute;t xẻ sang hai b&ecirc;n, nếu bạn cố g&agrave;i sẽ l&agrave;m hư phom d&aacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/a2.JPG\" style=\"height:489px; width:609px\" /></p>\r\n\r\n<p><em>Vest 2 n&uacute;t</em></p>\r\n\r\n<p><strong>3. Loại ba n&uacute;t</strong></p>\r\n\r\n<p>Mọi việc c&oacute; vẻ dần phức tạp l&ecirc;n nhưng đừng lo, bởi v&igrave; lu&ocirc;n c&oacute; những quy tắc chuẩn để ghi nhớ. N&uacute;t giữa lu&ocirc;n g&agrave;i (trừ khi ngồi), n&uacute;t tr&ecirc;n c&ugrave;ng c&oacute; thể g&agrave;i để tr&ocirc;ng lu&ocirc;n lịch thiệp hoặc kh&ocirc;ng để tạo sự ph&oacute;ng kho&aacute;ng trẻ trung. V&agrave; dĩ nhi&ecirc;n chiếc n&uacute;t cuối c&ugrave;ng lu&ocirc;n kh&ocirc;ng đ&oacute;ng.</p>\r\n\r\n<p><strong>4.&nbsp;H&agrave;ng n&uacute;t tay &aacute;o suit jacket (suit cuffs)</strong></p>\r\n\r\n<p>Những bộ suit bespoke (may đo) lu&ocirc;n c&oacute; h&agrave;ng khuy ngay cổ tay &aacute;o kho&aacute;c, n&oacute; l&agrave; một di sản từ xa xưa khi đồ suit l&agrave; thứ trang phục h&agrave;ng ng&agrave;y của nam giới. Bất kể l&agrave; người lao động ch&acirc;n tay, nh&acirc;n vi&ecirc;n b&agrave;n giấy hay thậm ch&iacute; l&agrave; b&aacute;c sĩ giải phẫu th&igrave; đ&agrave;n &ocirc;ng lu&ocirc;n mặc suit. H&agrave;ng n&uacute;t ở cổ tay &aacute;o c&oacute; t&ecirc;n l&agrave; functioning cuff buttons, c&oacute; thể mở ra để xắn ống tay &aacute;o l&ecirc;n để hạn chế việc vấy bẩn hay hư hỏng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://biluxury.vn/media/a7.JPG\" style=\"height:249px; width:357px\" /></p>\r\n\r\n<p><em>H&agrave;ng khuy tay &aacute;o may đo thường sẽ c&oacute; thể &ldquo;đ&oacute;ng-mở&rdquo; được.</em></p>\r\n\r\n<p>Nhưng ng&agrave;y nay, n&oacute; trở th&agrave;nh chi tiết trang tr&iacute; cho bộ suit chứ kh&ocirc;ng c&ograve;n mang &yacute; nghĩa ban đầu. Ri&ecirc;ng đối với những bộ suit bespoke đắt tiền, chi tiết h&agrave;ng n&uacute;t được may đo tỉ mỉ v&agrave; c&oacute; thể &ldquo;c&agrave;i &ndash; đ&oacute;ng&rdquo; được, đ&acirc;y cũng l&agrave; dấu hiệu nhận biết gi&aacute; trị của bộ c&aacute;nh người ấy mặc tr&ecirc;n người.</p>\r\n', '15434164861543416486tin-03.jpg', 0);
